# main contains a proxy for entering permissible functions.
function main() {
  case "$1" in
    install)
      if [ "$(is_server_running)" == "true" ]; then
        echo "${ER} cannot install on started server"; return 0
      fi

      local branch="none"
      local fixes="true"

      while [[ -n "$2" ]]; do
        case "$2" in
          --branch|-b) branch="$3" ;;
          --no-fixes|-n) fixes="false" ;;
          dependencies|dep)
            echo "${INFO} Installing dependencies.."

            install_dependencies
            return ;;
          folders)
            echo "${INFO} Creating required folders.."

            create_folders
            return ;;
          utils)
            echo "${INFO} Installing additional utils.."

            install_range_builder
            install_rcon
            return ;;
          prepare)
            echo "${INFO} Installing dependencies, additional utils and creating required folders.."

            install_dependencies
            create_folders
            install_range_builder
            install_rcon
            return ;;
          server)
            while [[ -n "$3" ]]; do
              case "$3" in
                --branch|-b) branch="$4" ;;
                --no-fixes|-n) fixes="false" ;;
              esac

              shift
            done

            echo "${INFO} Installing only Project Zomboid server from \"${branch}\" branch.."

            install_server "${branch}"
            if [ "${fixes}" == "true" ]; then
              fix_options
              fix_args
            fi
            return ;;
          --help)
            print_help_install
            return ;;
          fix)
            echo "${INFO} Apply fixes.."
            fix_options
            fix_args
            return ;;
        esac

        shift
      done

      echo "${INFO} Installing dependencies, additional utils, creating required folders and installs PZ server.."
      install_dependencies
      create_folders
      install_range_builder
      install_rcon
      install_server "${branch}"
      if [ "${fixes}" == "true" ]; then
        fix_options
        fix_args
      fi ;;
    update)
      if [ "$(is_server_running)" == "true" ]; then
        echo "${ER} cannot update on started server"; return 0
      fi

      local fixes="true"

      while [[ -n "$2" ]]; do
        case "$2" in
          --no-fixes|-n) fixes="false" ;;
          --help)
            print_help_update
            return ;;
        esac

        shift
      done

      echo "${INFO} Updating Project Zomboid server.."

      update_server

      if [ "${fixes}" == "true" ]; then
        fix_options
        fix_args
      fi ;;
    sync)
      # TODO: Deprecate me.
      config_pull;;
    start)
      local no_screen="false"

      while [[ -n "$2" ]]; do
        case "$2" in
          --no-screen|-n) no_screen="true";;
          --help)
            print_help_start
            return ;;
        esac

        shift
      done

      start "${no_screen}";;
    stop)
      local when
      local fixes

      while [[ -n "$2" ]]; do
        case "$2" in
          now|kill) when="$2";;
          --fixes|-f|fix) fixes="fix";;
          --help)
            print_help_stop
            return ;;
        esac

        shift
      done

      shutdown_wrapper "stop" "${when}" "${fixes}";;
    restart)
      local when
      local fixes

      while [[ -n "$2" ]]; do
        case "$2" in
          now|kill) when="$2";;
          --fixes|-f|fix) fixes="fix";;
          --help)
            print_help_restart
            return ;;
        esac

        shift
      done

      shutdown_wrapper "restart" "${when}" "${fixes}";;
    autorestart)
      autorestart;;
    console)
      console;;
    cmd)
      local proto="rcon"
      local command

      while [[ -n "$2" ]]; do
        case "$2" in
          --screen|-s) proto="screen" ;;
          --rcon|-r) proto="rcon" ;;
          --help)
            print_help_cmd
            return ;;
          *)
            if [ -n "${command}" ]; then
              echoerr "to many arguments"; return
            fi
            command="$2" ;;
        esac

        shift
      done

      if [ "${proto}" == "rcon" ]; then
         rconcmd "${command}"
      else
         screencmd "${command}"
      fi ;;
    kickusers)
      kickusers;;
    delfile)
      while [[ -n "$2" ]]; do
        case "$2" in
          manifest)
            delete_mods_manifest
            return ;;
          zombies)
            delete_zombies
            return ;;
          --help|*)
            print_help_delfile
            return ;;
        esac

        shift
      done

      print_help_delfile ;;
    map_regen)
      map_regen "$2" "$3";;
    map_copy)
      map_copy "$2" "$3" "$4";;
    map_copyto)
      map_copyto "$2" "$3" "$4" "$5";;
    range)
      local bottom="$3"
      if [ "${bottom}" == "-" ]; then
        bottom=$4
      fi

      range "$2" "${bottom}";;
    backup)
      backup "$2";;
    log)
      log_search "$2" "$3" "$4" "$5";;
    clog)
      clog_search "$2" "$3" "$4" "$5";;
    sql)
      fn_sqlite "$2";;
    vehicles)
      fn_vehicles;;
    restore_players)
      restore_players "$2";;
    --variables|--vars)
      print_variables;;
    --version)
      print_version;;
    --help)
      print_help;;
    --test)
      echo "test"
      is_admin_exists
  esac
}

